svg.script:

  var SVGDocument = null;
  var SVGRoot = null;

  var TrueCoords = null;
  var GrabPoint = null;

  var BackDrop = null;
  var DragTarget = null;
  var Left;
  var Right;
  var OldBound;
  var NowBound;
  var OldLine;
  var NowLine;
  var MidLine;

  var xLeft;
  var xRight;
  var checkLeft = 1;
  var checkRight = 1140;

  var lowBound = -1500;
  var highBound = 2010;
  var pixDistance;
  var scale;
  var ratio;
  var time;


  function Init(evt){
   SVGDocument = evt.target.ownerDocument;
   SVGRoot = SVGDocument.documentElement;

   TrueCoords = SVGRoot.createSVGPoint();
   GrabPoint = SVGRoot.createSVGPoint();

   BackDrop = SVGDocument.getElementById('BackDrop');
   TextBox = SVGDocument.getElementById('TextBox');
   Left = SVGDocument.getElementById('LeftSlider');
   Right = SVGDocument.getElementById('RightSlider');
   TLeft = SVGDocument.getElementById('ChangeL');
   TRight = SVGDocument.getElementById('ChangeR');
   OldBound = SVGDocument.getElementById('LeftBound');
   NowBound = SVGDocument.getElementById('RightBound');
   OldLine = SVGDocument.getElementById('LPath');
   NowLine = SVGDocument.getElementById('RPath');
   MidLine = SVGDocument.getElementById('MiddlePath');
  }

  function Grab(evt){
   var targetElement = evt.target;
   if(targetElement == Left || targetElement == Right){
    DragTarget = targetElement;
    DragTarget.parentNode.appendChild( DragTarget );
    DragTarget.setAttributeNS(null, 'pointer-events', 'none');

    var transMatrix = DragTarget.getCTM();
    GrabPoint.x = TrueCoords.x - Number(transMatrix.e);
   }
  };

  function Drag(evt){

   if(DragTarget){
       DragTarget.setAttributeNS(null, 'cx', evt.clientX);
   }
  if (DragTarget == Left) {
    TLeft.setAttributeNS(null, 'x', evt.clientX);
    ChangeWords(evt.clientX, 'L');
}
else if (DragTarget == Right) {
   TRight.setAttributeNS(null, 'x', evt.clientX);
   ChangeWords(evt.clientX, 'R');
}
    }


  function Drop(evt){
   if(DragTarget){
    var targetElement = evt.target;
    DragTarget.setAttributeNS(null, 'pointer-events', 'all');
    DragTarget = null;
   }
  };



  function ChangeWords(xVal, slider){
   Changeable = SVGDocument.getElementById('Change' + slider);
   newVal = Scale(xVal, slider);
   if(newVal < 0){
    newVal = newVal * -1;   
    newText = SVGDocument.createTextNode(newVal + ' BCE');
   }
   else{
    newText = SVGDocument.createTextNode(newVal + ' CE');
   }
   Changeable.replaceChild(newText, Changeable.firstChild);  
  };

  function Scale(x, slider){
   scale = highBound - lowBound; // time bounds
   RIGHT_X = 1220;//pixels
   LEFT_X = 50;//pixels
   pixDistance = RIGHT_X - LEFT_X;
   ratio = (x - LEFT_X) / pixDistance;
   time = Math.round(ratio * scale) + lowBound;
   return time;
  };
